<?phprequire( 'GCMFunctions.php' );global $db;// Create DB ConnectioncreateDBConnection();$iid = $_POST['iid'];$ip = $_POST['ip_addr'];$app_url = $_POST['app_url'];if($iid == null){	$iid = "null";}$app_details = get_application_details_by_blog_url($app_url);$app_id = $app_details['ID'];$query = "INSERT INTO hanu_active_devices (AppID,InstanceID, IP_Addr) VALUES (:app_id,:iid,:ip)";$stmt = $db->prepare($query);$sqlVars = array();$sqlVars[':iid'] = $iid;$sqlVars[':ip'] = $ip;$sqlVars[':app_id'] = $app_id;$stmt->execute($sqlVars);$stmt = null;?>