<?phprequire( 'GCMFunctions.php' );// Create DB ConnectioncreateDBConnection();$package = $_POST['package'];$regId = $_POST['regid'];$iid = $_POST['iid'];$tz = $_POST['tz'];$app_version = $_POST['app_version'];if($app_version == ""){	$app_version = 0;}$platform = $_POST['platform'];if($platform == ""){	$platform = "Android";}// Validate dataif($regId == null || empty($regId) || strcmp($regID,"null") == 0){	die('Wrong Reg ID');}if($iid == null || empty($iid) || strcmp($iid,"null") == 0){	die('Wrong Reg Instance ID');}// Get application details$app_details = get_application_details($package);if($app_details == null){	die("Application not registered with Hanu-Droid");}$app_id = $app_details['ID'];// Check if entry already exists.$reg_id_details = get_regid_details($regId);if($reg_id_details == null){	// Does not exist. So save.	$id = save_regid_details($app_id, $regId, $iid, $tz, $app_version,$platform);	if($id > 0){		// Success		echo "Success";	}	else{		die('Could not register!');	}}else{	// Already Exists Nothing to do	echo "Success";}?>